import groovy.sql.Sql
def driver="oracle.jdbc.driver.OracleDriver"
def path="jdbc:oracle:thin:@lonchdb01.corp.ttc:1521/CH"
def username='ch_qa'
def password='ch_qa'
def sql = Sql.newInstance(path,username,password,driver)
//def stmt  = "select count(*) from brand"
//sql.execute(stmt)
def tim = sql.firstRow("select TOUR_CODE from CONTENT_REPOSITORY where REPOSITORY_STATUS = 'TourDepartureOnly'")
def tdm = sql.firstRow("select TOUR_CODE from CONTENT_REPOSITORY where REPOSITORY_STATUS = 'TourInfoOnly'")

def bti = sql.firstRow(   "select CODE from selling_company where ID=(SELECT selling_company_id from SELLINGCOMPANY_CONTENT where content_id=(SELECT CONTENT_REPOSITORY.ID from content_repository where Tour_Code='"+tim.TOUR_CODE+"'))")
def btd = sql.firstRow(   "select CODE from selling_company where ID=(SELECT selling_company_id from SELLINGCOMPANY_CONTENT where content_id=(SELECT CONTENT_REPOSITORY.ID from content_repository where Tour_Code='"+tdm.TOUR_CODE+"'))") 

//row=sql.firstRow(stmt)
testRunner.testCase.testSuite.project.setPropertyValue("TI_MISSING", tim.TOUR_CODE)
testRunner.testCase.testSuite.project.setPropertyValue("TD_MISSING", tdm.TOUR_CODE)

testRunner.testCase.testSuite.project.setPropertyValue("SC_TIM", bti.CODE)
testRunner.testCase.testSuite.project.setPropertyValue("SC_TDM", btd.CODE)

if(tim!=null && tdm!=null && bti!=null && btd!=null){
	log.info("Pomyslnie pobrano dane z DB")
}else{
	log.info("blad odczytu z bazy!")
}

