declare namespace soap='http://schemas.xmlsoap.org/soap/envelope/';
declare namespace ns1='http://www.ttsl.com/MarketLocalisedTourData/2010/11/1.0';
declare namespace ns3='http://contenthub.travcorp.com/tour_data/2010/11/1.0';
declare namespace ns2='http://CCAPI.TravelCorp.com/';
//ns2:SearchToursResponse[1]/ns2:SearchToursResult[1]/ns2:Header[1]/ns2:NumberOfRecords[1]

con:config.script:
def response = testRunner.testCase.testSteps['Properties'].getPropertyValue("response1")
def xml = new XmlSlurper().parseText( response )
def arrLength = xml.Body.SearchToursResponse.SearchToursResult.Results.SearchTourResultsItem.size()
Double[] list = new Double[arrLength]
for(int i = 0; i<arrLength; i++){
	Double cena = xml.Body.SearchToursResponse.SearchToursResult.Results.SearchTourResultsItem[i].Price.toDouble()
	list[i]=cena;
}
boolean s=true;
for(int j =0 ; j<arrLength-1; j++){
	if(list[j]>list[j+1]){
		s=false;
	}
}
if(s){
	log.info("sortowanie OK!")
}else{
	log.info("Blad sortowania!")
	testRunner.fail()
}

